import React from "react";
import tickets from "./ticket-data.json";
import { Fragment } from "react";
import TicketRow from "../components/TicketRow";

class Tickets extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            data: tickets
        }
    }

    fetchAssets() {
        var endpoint_url = "https://www.students.cs.ubc.ca/~shasan01/sql_commands.php";
        var formData = new FormData();
        formData.append("query", 'select * from assets;')
        // set this.state.data to be our assets
    }

    render() {
        return (
            <div class="container-fluid">
                <h1>Open Tickets</h1>
                <div class="mt-2 bg-light round">
                    <div class="px-3 mt-3 mb-3">
                        <table>
                            <tr>
                                <th>Ticket #</th>
                                <th>Status</th> 
                                <th>Date Created</th>
                                <th>Submitted By</th>
                                <th>Reserved Assets</th>
                            </tr>
                            {
                                this.state.data.map((ticket) => (
                                    <Fragment>
                                        <TicketRow ticket={ticket} resolved={false}/> 
                                    </Fragment>
                                ))
                            }
                        </table>
                    </div>
                </div>
                <h1>Resolved Tickets</h1>
                <div class="mt-2 bg-light round">
                    <div class="px-3 mt-3 mb-3">
                        <table>
                            <tr>
                                <th>Ticket #</th>
                                <th>Status</th> 
                                <th>Date Resolved</th>
                                <th>Submitted By</th>
                                <th>Reserved Assets</th>
                            </tr>
                            {
                                this.state.data.map((ticket) => (
                                    <Fragment>
                                        <TicketRow ticket={ticket} resolved={true}/> 
                                    </Fragment>
                                ))
                            }
                        </table>
                    </div>
                </div>
            </div>
        );
    }
}

export default Tickets;
