import React from "react";
import catalogue_items from "./catalogue_items.json";
import { Fragment } from "react";
import PurchasableAsset from "../components/PurchasableAsset";

const id = "";
const name = "";
const model = "";
const insert_into = "";
const cost = "";

class Catalogue extends React.Component {
    static get constant1() {
        return id;
    }
    static get constant2() {
        return name;
    }
    static get constant3() {
        return model;
    }
    static get constant4() {
        return insert_into;
    }
    static get constant5() {
            return cost;
        }

    constructor(props) {
        super(props);
        this.state = {
            data: catalogue_items,
            id: '',
            name: '',
            model: '',
            insert_into: '',
            cost: ''
        }
    }

    // identify the item ID of the row clicked
    // using the ID get all the item attributes
    // send all the attributes data via POST

    /*
    handleClick = (event,  item) => {
        event.preventDefault();
        this.setState({id: item.id});
    }
    */

    handlePurchaseAsset = (event, item) => {
        event.preventDefault();
 //       var cataloguePage = this;
        var url = "https://www.students.cs.ubc.ca/~ehsieh25/sql_commands.php";
        var formData = new FormData();

        console.log("print state"); //debugging
        console.log(this.state.id); //debugging
        console.log(this.state.name); //debugging
        console.log("print item"); //debugging
        console.log(item.id); //debugging
        console.log(item.name); //debugging
        console.log("print const");


        let id = item.id;
        let name = item.name;
        console.log(id); //debugging
        console.log(name); //debugging

        this.setState({model: item.model});
        this.setState({insert_into: item.insert_into});
        this.setState({cost: item.cost});
        console.log(this.state.id); //debugging
        console.log(this.state.name); //debugging

        formData.append("catalogue_id", id);
        formData.append("catalogue_name", name);
        formData.append("catalogue_model", this.state.model);
        formData.append("catalogue_insert_into", this.state.insert_into);
        formData.append("catalogue_cost", this.state.cost);
        console.log("check3"); //debugging

        const requestOptions = {
            method: "POST",
            body: formData,
        };

        fetch(url, requestOptions)
            .then(function (response) {
                return response.text();
            })
         //   .then(function (body) {
         //       const body_json = JSON.parse(body);
         //       cataloguePage.setState({data: body_json});
       // });
        console.log("check4") //debugging
    }

    render() {
        return (
            <div class="container-fluid">
                <h1>Asset Store</h1>
                <div class="mt-2 bg-light round">
                    <div class="px-3 mt-3 mb-3">
                        <form>
                            <table>
                                <tr>
                                    <th>Item</th>
                                    <th>Model</th> 
                                    <th>Price</th>
                                </tr>
                                {
                                    this.state.data.map((item) => (
                                        <Fragment>
                                            <PurchasableAsset item={item}
                                            handlePurchaseAsset={this.handlePurchaseAsset}
                                            />
                                        </Fragment>
                                    ))
                                }
                            </table>
                        </form>
                    </div>
                </div>
            </div>
        );
    }
}

export default Catalogue;
