import React from "react";

// sample data:
const data = [
    {inv_number : 400001, cost: 55.00, name: "Soldering Iron", model: "Weller"},
    {inv_number : 400002, cost: 1200.00, name: "Microscope", model: "Nikon"},
    {inv_number : 400003, cost: 35.00, name: "iFixit tool kit", model: "iFixit"}
]

function Equipments() {
    return (
        <div class="container-fluid">
            <div class="mt-2 bg-light round">
                <div class="px-3 mt-3 mb-3">
                <h1 class="display-4">Lab Equipments</h1>
                    <table>
                        <tr>
                            <th>Inventory #</th>
                            <th>Cost</th>
                            <th>Name</th>
                            <th>Model</th>
                        </tr>
                        {
                            data.map((val, key) => {
                                return (
                                    <tr key={key}>
                                        <td>{val.inv_number}</td>
                                        <td>${val.cost.toFixed(2)}</td>
                                        <td>{val.name}</td>
                                        <td>{val.model}</td>
                                    </tr>
                                )
                            }
                            )
                        }
                    </table>
                </div>
            </div>
            <form method="post" class="text-center">
                <input type="button" class="my-2 btn btn-primary" name="add-asset" value="Add Asset"></input>
                <input type="button" class="my-2 mx-3 btn btn-danger" name="add-asset" value="Remove Asset"></input>
            </form>
        </div>
    );
}

export default Equipments;
