import React from "react";
import assets from "./mock-data.json";
import { Fragment } from 'react';
import AssetRow from "../components/AssetRow";

// sample data:
// using the mock-data.json file to test the functionalities.

class Assets extends React.Component {
    constructor(props) {
        super(props);
        this.handleGetAssets();
        this.state = {
            data: assets
        }
    }

    handleGetAssets = () => {
        var assetPage = this;
        var url = "https://www.students.cs.ubc.ca/~ehsieh25/sql_commands.php";
        var formData = new FormData();
        formData.append("get", "Purchased_Asset");

        const requestOptions = {
            method: "POST",
            body: formData,
        };

        fetch(url, requestOptions)
            .then(function (response) {
                return response.text();
            })
            .then(function (body) {
                const body_json = JSON.parse(body);
                assetPage.setState({data: body_json});
        });
    }

    render() {
        return (
            <div class="container-fluid">
                <h1>Assets</h1>
                <div class="mt-2 bg-light round">
                    <div class="px-3 mt-3 mb-3">
                        <table>
                            <tr>
                                <th>Inventory #</th>
                                <th>Cost</th>
                                <th>Name</th>
                                <th>Model</th>
                            </tr>
                            {
                                this.state.data.map((asset) => (
                                    <Fragment>
                                        <AssetRow asset={asset}/> 
                                    </Fragment>
                                ))
                            }
                        </table>
                    </div>
                </div>
                <div class="text-center">
                    <form>
                        <a href="/catalogue" class="my-2 btn btn-primary">Add Asset</a>
                    </form>
                </div>
            </div>
        );
    }
}

export default Assets;
