import React from "react";
import {useState} from 'react';
import { nanoid } from 'nanoid';

// sample data:
// query data to hide data, only show assets belonging to a specific department
const data = [
    { inv_number: 12345678, cost: 1600.25, name: "M1 Macbook Air", model: "512 GB" },
    { inv_number: 23456789, cost: 1200.00, name: "M1 Macbook Air", model: "128 GB" },
    { inv_number: 45678901, cost: 1500.00, name: "M1 Macbook Air", model: "256 GB" }
]
/*
const [addFormData, setAddFormData] = useState({
    inventoryNum: '',
    cost: '',
    name: '',
    model: ''
})

const handleAddFormChange = (event) => {
    event.preventDefault();

    const fieldName = event.target.getAttribute('name');
    const fieldValue = event.target.value;

    const newFormData = { ...addFormData};
    newFormData[fieldName] = fieldValue;

    setAddFormData(newFormData);
}
*/

function Assets() {
    const [datas, setDatas] = useState(data);
    const [addFormData, setAddFormData] = useState({
        inventoryNum: '',
        cost: '',
        name: '',
        model: ''
    })
    const handleAddFormChange = (event) => {
        event.preventDefault();

        const fieldName = event.target.getAttribute('name');
        const fieldValue = event.target.value;

        const newFormData = { ...addFormData};
        newFormData[fieldName] = fieldValue;

        setAddFormData(newFormData);
    }

    const handleAddFormSubmit = (event) => {
        event.preventDefault();

        const newData = {
            id: nanoid(),
            inventoryNum: addFormData.inventoryNum,
            cost: addFormData.cost,
            name: addFormData.name,
            model: addFormData.model
        }

        const newDatas = [...datas, newData];
        setDatas(newDatas);  

    }


    return (
        <div class="container-fluid">
            <div class="mt-2 bg-light round">
                <div class="px-3 mt-3 mb-3">
                    <h1 class="display-4">Assets</h1>
                    <table>
                        <tr>
                            <th>Inventory #</th>
                            <th>Cost</th>
                            <th>Name</th>
                            <th>Model</th>
                        </tr>
                        {
                            data.map((val, key) => {
                                return (
                                    <tr key={key}>
                                        <td>{val.inv_number}</td>
                                        <td>${val.cost.toFixed(2)}</td>
                                        <td>{val.name}</td>
                                        <td>{val.model}</td>
                                    </tr>
                                )
                            }
                            )
                        }
                    </table>
                </div>
            </div>
            <div>
                <form onSumit={handleAddFormSubmit}>
                    <input 
                        type="text" 
                        name="inventoryNum" 
                        required="required" 
                        placeholder="Inventory #"
                        onChange={handleAddFormChange}
                    />
                    <input 
                        type="text" 
                        name="cost" 
                        required="required" 
                        placeholder="Cost"
                        onChange={handleAddFormChange}
                    />
                    <input 
                        type="text" 
                        name="name" 
                        required="required" 
                        placeholder="Name"
                        onChange={handleAddFormChange}
                    />
                    <input 
                        type="text" 
                        name="model" 
                        required="required" 
                        placeholder="Model"
                        onChange={handleAddFormChange}
                    />
                </form>
            </div>
            <form method="post" class="text-center">
                <input type="submit" class="my-2 btn btn-primary" name="add-asset" value="Add Asset"></input>
                <input type="button" class="my-2 mx-3 btn btn-danger" name="add-asset" value="Remove Asset"></input>
            </form>
        </div>
    );
}

export default Assets;
