import React from "react";

//sample data:
const data = [
    {inv_number : 100010, cost: 500.00, name: "MATLAB R2022b", model: "2022, MacOS", licenseNumber: "KZ9AMK8E"},
    {inv_number : 100011, cost: 500.00, name: "MATLAB R2022b", model: "2022, MacOS", licenseNumber: "QVH3USDY"},
    {inv_number : 100012, cost: 500.00, name: "MATLAB R2022b", model: "2022, Windows", licenseNumber: "KKA2AIEX"}
]

function Software() {
    return (
        <div class="container-fluid">
            <div class="mt-2 bg-light round">
                <div class="px-3 mt-3 mb-3">
                <h1 class="display-4">Software License</h1>
                    <table>
                        <tr>
                            <th>Inventory #</th>
                            <th>Cost</th>
                            <th>Name</th>
                            <th>Model</th>
                            <th>License Number</th>
                        </tr>
                        {
                            data.map((val, key) => {
                                return (
                                    <tr key={key}>
                                        <td>{val.inv_number}</td>
                                        <td>${val.cost.toFixed(2)}</td>
                                        <td>{val.name}</td>
                                        <td>{val.model}</td>
                                        <td>{val.licenseNumber}</td>
                                    </tr>
                                )
                            }
                            )
                        }
                    </table>
                </div>
            </div>
            <form method="post" class="text-center">
                <input type="button" class="my-2 btn btn-primary" name="add-asset" value="Add Asset"></input>
                <input type="button" class="my-2 mx-3 btn btn-danger" name="add-asset" value="Remove Asset"></input>
            </form>
        </div>
    );
}

export default Software;
