import React from "react";
import catalogue_items from "./catalogue_items.json";
import { Fragment } from "react";
import PurchasableAsset from "../components/PurchasableAsset";

class Catalogue extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            data: catalogue_items,
            id: '',
            name: '',
            model: '',
            insert_into: '',
            cost: ''
        }
    }

    // identify the item ID of the row clicked
    // using the ID get all the item attributes
    // send all the attributes data via POST

    /*
    handleClick = (event,  item) => {
        event.preventDefault();

        this.setState({id: item.id});
        this.setState({name: item.name});
        this.setState({model: item.model});
        this.setState({insert_into: item.insert_into});
        this.setState({cost: item.cost});

        //formData.append("catalogue_id", this.state.id);
        formData.append("catalogue_id", "get");
        formData.append("catalogue_name", this.state.name);
        formData.append("catalogue_model", this.state.model);
        formData.append("catalogue_insert_into", this.state.insert_into);
        formData.append("catalogue_cost", this.state.cost);
    }
    */

    handlePurchaseAsset = (event, item) => {
        event.preventDefault();
        var cataloguePage = this;
        var url = "https://www.students.cs.ubc.ca/~ehsieh25/sql_commands.php";
        var formData = new FormData();
        this.setState({id: item.id});
        this.setState({name: item.name});
        this.setState({model: item.model});
        this.setState({insert_into: item.insert_into});
        this.setState({cost: item.cost});

        //formData.append("catalogue_id", this.state.id);
        formData.append("catalogue_id", "get");
        formData.append("catalogue_name", this.state.name);
        formData.append("catalogue_model", this.state.model);
        formData.append("catalogue_insert_into", this.state.insert_into);
        formData.append("catalogue_cost", this.state.cost);

        const requestOptions = {
            method: "POST",
            body: formData,
        };

        fetch(url, requestOptions)
            .then(function (response) {
                return response.text();
            })
            .then(function (body) {
                const body_json = JSON.parse(body);
                cataloguePage.setState({data: body_json});
        });
    }

    render() {
        return (
            <div class="container-fluid">
                <h1>Asset Store</h1>
                <div class="mt-2 bg-light round">
                    <div class="px-3 mt-3 mb-3">
                        <form>
                            <table>
                                <tr>
                                    <th>Item</th>
                                    <th>Model</th> 
                                    <th>Price</th>
                                </tr>
                                {
                                    this.state.data.map((item) => (
                                        <Fragment>
                                            <PurchasableAsset item={item}
                                            handleClick={this.handlePurchaseAsset}
                                            />
                                        </Fragment>
                                    ))
                                }
                            </table>
                        </form>
                    </div>
                </div>
            </div>
        );
    }
}

export default Catalogue;
