import React from "react";

// sample data:
const data = [
    {inv_number : 200010, cost: 19.99, name: "Hex Spacer Standoffs", model: "Jucoan M3", type:"standoff", size:"M3x12mm"},
    {inv_number : 200011, cost: 0.72, name: "Bolt Size Flat Washers", model: "Paulin", type:"washer", size:"1/2 inch"},
    {inv_number : 200012, cost: 1500.00, name: "M1 Macbook Air", model: "256 GB", type:"type", size:"size"}
]

function Hardware() {
    return (
            <div class="container-fluid">
                <div class="mt-2 bg-light round">
                    <div class="px-3 mt-3 mb-3">
                    <h1 class="display-4">Hardware</h1>
                        <table>
                            <tr>
                                <th>Inventory #</th>
                                <th>Cost</th>
                                <th>Name</th>
                                <th>Model</th>
                                <th>Type</th>
                                <th>Size</th>
                            </tr>
                            {
                                data.map((val, key) => {
                                    return (
                                        <tr key={key}>
                                            <td>{val.inv_number}</td>
                                            <td>${val.cost.toFixed(2)}</td>
                                            <td>{val.name}</td>
                                            <td>{val.model}</td>
                                            <td>{val.type}</td>
                                            <td>{val.size}</td>
                                        </tr>
                                    )
                                }
                                )
                            }
                        </table>
                    </div>
                </div>
                <form method="post" class="text-center">
                    <input type="button" class="my-2 btn btn-primary" name="add-asset" value="Add Asset"></input>
                    <input type="button" class="my-2 mx-3 btn btn-danger" name="add-asset" value="Remove Asset"></input>
                </form>
            </div>
    );
}

export default Hardware;
