import React from "react";


class Dashboard extends React.Component {
    constructor(props) {
        super(props);
        this.handleFormSubmit();
        this.state = {
            v1: '',
            v2: '',
        }
    }

    handleFormSubmit = (event) => {
        var dashboardPage = this;
        var url = "https://www.students.cs.ubc.ca/~shasan01/sql_commands.php";
        var formData = new FormData();
        formData.append('v1', this.state.v1);
        formData.append('v2', this.state.v2);

        const requestOptions = {
            method: "POST",
            body: formData,
        };

        fetch(url, requestOptions)
            .then(function (response) {
                return response.text();
            })
            .then(function (body) {
                const body_json = JSON.parse(body);
                dashboardPage.setState({data: body_json});
        });
    };

    render() {
        return (
            <>
            <div>
                <h1>this is the dashboard</h1>
                <form>
                    <input 
                    id="v1" name="v1"
                    value={this.state.v1}
                    onChange={e => this.setState({v1: e.target.value})}
                    />
                    <select name="action">
                        <option value="plus">+</option>
                        <option value="minus">-</option>
                    </select>
                    <input 
                    id="v2" name="v2"
                    value={this.state.v2}
                    onChange={e => this.setState({v2: e.target.value})}
                    />
                    <input 
                    type="submit" name="submit" value="Submit"
                    onClick={e => this.handleFormSubmit(e)}/>
                </form>
            </div>
            </>
        )
    }
}


export default Dashboard;
