import React from "react";

const TicketRow = ({ ticket, resolved }) => {
    if ((ticket.status == "Resolved") == resolved) {
        return (
            <tr>
                <td>{ticket.ticket_number}</td>
                <td>{ticket.status == null ? "Available" : ticket.status}</td>
                <td>{resolved ? ticket.date_resolved : ticket.date_created}</td>
                <td>{ticket.submitted_by}</td>
                <td>
                    {
                        ticket.reserved_assets.map((asset, index) => (
                            <button type="button" class="btn btn-light">
                                {asset}
                            </button>
                        ))
                    }
                </td>
            </tr>
        )
    } else {
        return null;
    }
}

export default TicketRow;
