import React from "react";
import data from "./mock-data.json";
import {useState} from 'react';
import {Fragment} from 'react';
import { nanoid } from 'nanoid';
import ReadOnlyRow from "../components/ReadOnlyRow";
import EditableRow from "../components/EditableRow";

// sample data:
// query data to hide data, only show assets belonging to a specific department

function Assets() {
    const [assets, setAssets] = useState(data);
    const [addFormData, setAddFormData] = useState({
        inventoryNum: '',
        cost: '',
        name: '',
        model: ''
    })
    const [editFormData, setEditFormData] = useState({
        inventoryNum: '',
        cost: '',
        name: '',
        model: ''
    })
    const [editAssetId, setEditAssetId] = useState(null);

    const handleAddFormChange = (event) => {
        event.preventDefault();

        const fieldName = event.target.getAttribute('name');
        const fieldValue = event.target.value;

        const newFormData = { ...addFormData};
        newFormData[fieldName] = fieldValue;

        setAddFormData(newFormData);
    }

    const handleEditFormChange = (event) => {
        event.preventDefault();

        const fieldName = event.target.getAttribute("name");
        const fieldValue = event.target.value;

        const newFormData = { ...editFormData }
        newFormData[fieldName] = fieldValue;

        setEditFormData(newFormData);
    }

    const handleAddFormSubmit = (event) => {
        event.preventDefault();

        const newAsset = {
            id: nanoid(),
            inventoryNum: addFormData.inventoryNum,
            cost: addFormData.cost,
            name: addFormData.name,
            model: addFormData.model
        }

        const newAssets = [...assets, newAsset];
        setAssets(newAssets);  
    }

    const handleEditClick = (event, asset) => {
        event.preventDefault();
        setEditAssetId(asset.id);
    }

    return (
        <div class="container-fluid">
            <div class="mt-2 bg-light round">
                <div class="px-3 mt-3 mb-3">
                    <h1 class="display-4">Assets</h1>
                    <form>
                        <table>
                            <tr>
                                <th>Inventory #</th>
                                <th>Cost</th>
                                <th>Name</th>
                                <th>Model</th>
                                <th>Actions</th>
                            </tr>
                                {assets.map((asset) => (
                                    <Fragment>
                                        {editAssetId === asset.id ? (
                                            <EditableRow/> 
                                        ) : (
                                            <ReadOnlyRow asset={asset} 
                                            handleEditClick={handleEditClick}/> 
                                        )}
                                    </Fragment>
                                ))}
                        </table>
                    </form>
                </div>
            </div>
            <div>
                <h4>Add an Asset</h4>
                <form onSubmit={handleAddFormSubmit}>
                    <input 
                        type="number" 
                        name="inventoryNum" 
                        required="required" 
                        placeholder="Inventory#"
                        onChange={handleAddFormChange}
                    />
                    <input 
                        type="number" 
                        name="cost" 
                        required="required" 
                        placeholder="Cost"
                        onChange={handleAddFormChange}
                    />
                    <input 
                        type="text" 
                        name="name" 
                        required="required" 
                        placeholder="Name"
                        onChange={handleAddFormChange}
                    />
                    <input 
                        type="text" 
                        name="model" 
                        required="required" 
                        placeholder="Model"
                        onChange={handleAddFormChange}
                    />
                    <button type="submit" class="my-2 btn btn-primary">Add Asset</button>
                </form>
            </div>
            {/*
            <form method="post" class="text-center">
                <input type="submit" class="my-2 btn btn-primary" name="add-asset" value="Add Asset"></input>
                <input type="button" class="my-2 mx-3 btn btn-danger" name="add-asset" value="Remove Asset"></input>
            </form>
            */}
        </div>
    );
}

export default Assets;
