import React from "react";

// sample data:
const data = [
    {inv_number : 200010, cost: 550.00, name: "Intel Core i7-12700K", model: "12th Gen, 5.0 GHz", wattage: 95, serial_number: "2ZGfE5hU"},
    {inv_number : 200011, cost: 1200.00, name: "NVIDIA GeForce RTX 4090", model: "24GB, 2.52 GHz", wattage: 450, serial_number: "5XWbE7i7"},
    {inv_number : 200012, cost: 1500.00, name: "AMD Radeon RX 6950 XT", model: "16GB, 2.1 GHz", wattage: 300, serial_number: "8dbkpLLJ"}
]

function Electronics() {
    return (
        <div class="container-fluid">
            <div class="mt-2 bg-light round">
                <div class="px-3 mt-3 mb-3">
                <h1 class="display-4">Electronics</h1>
                    <table>
                        <tr>
                            <th>Inventory #</th>
                            <th>Cost</th>
                            <th>Name</th>
                            <th>Model</th>
                            <th>Wattage</th>
                            <th>Serial #</th>
                        </tr>
                        {
                            data.map((val, key) => {
                                return (
                                    <tr key={key}>
                                        <td>{val.inv_number}</td>
                                        <td>${val.cost.toFixed(2)}</td>
                                        <td>{val.name}</td>
                                        <td>{val.model}</td>
                                        <td>{val.wattage}</td>
                                        <td>{val.serial_number}</td>
                                    </tr>
                                )
                            }
                            )
                        }
                    </table>
                </div>
            </div>
            <form method="post" class="text-center">
                <input type="button" class="my-2 btn btn-primary" name="add-asset" value="Add Asset"></input>
                <input type="button" class="my-2 mx-3 btn btn-danger" name="add-asset" value="Remove Asset"></input>
            </form>
        </div>
    );
}

export default Electronics;
